import React, { useState } from 'react'
import { Form, FormGroup, Col, Input, Label, Button, Container, CardBody, Card, CardText } from 'reactstrap'
import '../../App.css'
import '../../index.css'

const Contact = () => {
        const [name, setName] = useState("")
        const [email, setEmail] = useState("")
        // const [subject, setSubject] = useState("")
        const [phoneNumber, setPhoneNumber] = useState("")
        const [content, setContent] = useState("")

        const formSubmit = async event => {
            event.preventDefault()
            console.log('ready to hit expressApp')
            const response = await fetch('http://localhost:4000/contact_forms/entries', {
                method: 'POST',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                  },
                body: JSON.stringify({name, email, phoneNumber, content})
            })
  /*
            const response = await fetch('http://google.ca', {
                method: 'GET',
                headers: null,
                body: null
            })
    */
            console.log('finish fetch call')
            const payload = await response.json()
            console.log('response code is' + response.status)
            if (response.status >= 400) {
                alert(`Oops! Error: ${payload.message} for fields: ${payload.invalid.join(",")}`)
            } else {
                alert(`Congrats! Submission submitted with id: ${payload.id}`)
                setName("")
                setEmail("")
                setPhoneNumber("")
                setContent("")
            }
        }
     return (
            <Container>
                {/* <Card className="text-white bg-secondary my-5 py-4 text-center">
                    <CardBody>
                        <CardText className="text-white m-0"> */}
                            <div className="row">
                            <div className="column left">
                            <h1> Student Sign Up </h1>

                        {/* </CardText>
                    </CardBody>
                </Card> */}
                <Form className="my-5" onSubmit={formSubmit}>

                    <FormGroup row>
                        {/* <Label for="nameEntry" sm={2}>First Name</Label> */}
                        <Col sm={10}>
                        <Input type="text" name="firstname" id="firstnameEntry" placeholder="First Name" required value={name} onChange={e => setName(e.target.value)}/>
                        </Col>
                    </FormGroup>

                    <FormGroup row>
                        {/* <Label for="nameEntry" sm={2}>Last Name</Label> */}
                        <Col sm={10}>
                        <Input type="text" name="lastname" id="lastnameEntry" placeholder="Last Name" required value={name} onChange={e => setName(e.target.value)}/>
                        </Col>
                    </FormGroup>

                    <FormGroup row>
                        {/* <Label for="nameEntry" sm={2}> Date of Birth </Label> */}
                        <Col sm={10}>
                        <Input type="date" name="dateofbirth" id="dobEntry" placeholder="Date of Birth" required value={name} onChange={e => setName(e.target.value)}/>
                        </Col>
                    </FormGroup>

                    <FormGroup row>
                        {/* <Label for="nameEntry" sm={2}> School Program </Label> */}
                        <Col sm={10}>
                        <Input type="text" name="schoolprogram" id="schoolprogramEntry" placeholder="School Name" required value={name} onChange={e => setName(e.target.value)}/>
                        </Col>
                    </FormGroup>


                    <FormGroup row>
                        {/* <Label for="emailEntry" sm={2}>Email</Label> */}
                        <Col sm={10}>
                        <Input type="email" name="email" id="emailEntry" placeholder="Username (Email)"  required value={email} onChange={e => setEmail(e.target.value) }/>
                        </Col>
                    </FormGroup>

                    <FormGroup row>
                        <Label for="emailEntry" sm={2}>Email</Label>
                        <Col sm={10}>
                        <Input type="email" name="email" id="emailEntry" placeholder="Enter email to contact"  required value={email} onChange={e => setEmail(e.target.value) }/>
                        </Col>
                    </FormGroup>

                    <FormGroup row>
                        {/* <Label for="passwordEntry" sm={2}>Password</Label> */}
                        <Col sm={10}>
                        <Input type="password" name="password" id="passwordEntry" placeholder="Password" onChange={e => setEmail(e.target.value) }/>
                        </Col>
                    </FormGroup>


                    <FormGroup check row>
                        <Col sm={{ size: 10, offset: 2 }}>
                        <Button color="success">Register</Button>
                        </Col>
                    </FormGroup>
                </Form>
                </div>
                </div>
            </Container>
          )




}





